import UIKit

final class InitialSetupDexcomG6SensorAgeWorker {
    func validateSensorAge(_ date: Date) -> Bool {
        return true
    }
    
    func saveSensorAge(_ date: Date) {
        CGMDevice.current.sensorStartDate = date
        CGMDevice.current.updateSensorIsStarted(true)
    }
}
