import UIKit

protocol SettingsAlertRootDisplayLogic: class {
    func displayLoad(viewModel: SettingsAlertRoot.Load.ViewModel)
}

class SettingsAlertRootViewController: BaseSettingsViewController, SettingsAlertRootDisplayLogic {
    var interactor: SettingsAlertRootBusinessLogic?
    
    // MARK: Object lifecycle
    
    required init?(coder aDecoder: NSCoder) {
        fatalError("Use regular init")
    }
    
    required init() {
        super.init()
        setup()
    }
    
    // MARK: Setup
    
    private func setup() {
        let viewController = self
        let interactor = SettingsAlertRootInteractor()
        let presenter = SettingsAlertRootPresenter()
        let router = SettingsAlertRootRouter()
        viewController.interactor = interactor
        interactor.presenter = presenter
        interactor.router = router
        presenter.viewController = viewController
        router.viewController = viewController
        router.dataStore = interactor
    }
    
    // MARK: IB
    
    // MARK: View lifecycle
    
    override func viewDidLoad() {
        super.viewDidLoad()
        doLoad()
    }
    
    // MARK: Do something
    
    private func doLoad() {
        setupUI()
        
        let request = SettingsAlertRoot.Load.Request(animated: false)
        interactor?.doLoad(request: request)
    }
    
    private func setupUI() {
        title = "settings_root_alert_title".localized
    }
    
    // MARK: Display
    
    func displayLoad(viewModel: SettingsAlertRoot.Load.ViewModel) {
        update(with: viewModel.tableViewModel, animated: viewModel.animated)
    }
}
