import UIKit

protocol NightscoutCloudBackfillDisplayLogic: AnyObject {
    func displayLoad(viewModel: NightscoutCloudBackfill.Load.ViewModel)
}

class NightscoutCloudBackfillViewController: BaseSettingsViewController, NightscoutCloudBackfillDisplayLogic {
    var interactor: NightscoutCloudBackfillBusinessLogic?
    var router: NightscoutCloudBackfillDataPassing?
    
    // MARK: Object lifecycle
    
    @available(*, unavailable)
    required init?(coder aDecoder: NSCoder) {
        fatalError("Use regular init")
    }
    
    required init() {
        super.init()
        setup()
    }
    
    // MARK: Setup
    
    private func setup() {
        let viewController = self
        let interactor = NightscoutCloudBackfillInteractor()
        let presenter = NightscoutCloudBackfillPresenter()
        let router = NightscoutCloudBackfillRouter()
        viewController.interactor = interactor
        interactor.presenter = presenter
        interactor.router = router
        presenter.viewController = viewController
        router.viewController = viewController
        router.dataStore = interactor
    }
    
    // MARK: IB
    
    // MARK: View lifecycle
    
    override func viewDidLoad() {
        super.viewDidLoad()
        setupUI()
        doLoad()
    }
    
    // MARK: Do something
    
    private func doLoad() {
        let request = NightscoutCloudBackfill.Load.Request()
        interactor?.doLoad(request: request)
    }
    
    private func setupUI() {
        title = "setttings_nightscout_cloud_backfill_title".localized
        
        let barButton = UIBarButtonItem(
            title: "settings_nightscout_cloud_backfill_send".localized,
            style: .done,
            target: self,
            action: #selector(doSend)
        )
        
        navigationItem.rightBarButtonItem = barButton
    }
    
    @objc private func doSend() {
        let request = NightscoutCloudBackfill.Send.Request()
        interactor?.doSend(request: request)
    }
    
    // MARK: Display
    
    func displayLoad(viewModel: NightscoutCloudBackfill.Load.ViewModel) {
        update(with: viewModel.tableViewModel)
    }
}
