import UIKit

protocol SettingsRootDisplayLogic: class {
    func displayLoad(viewModel: SettingsRoot.Load.ViewModel)
}

class SettingsRootViewController: BaseSettingsViewController, SettingsRootDisplayLogic {
    var interactor: SettingsRootBusinessLogic?
    var router: (NSObjectProtocol & SettingsRootRoutingLogic & SettingsRootDataPassing)?
    
    // MARK: Object lifecycle
    
    required init() {
        super.init()
        setup()
    }
    
    required init?(coder: NSCoder) {
        fatalError("Use regular init")
    }
    
    // MARK: Setup
    
    private func setup() {
        let viewController = self
        let interactor = SettingsRootInteractor()
        let presenter = SettingsRootPresenter()
        let router = SettingsRootRouter()
        viewController.interactor = interactor
        viewController.router = router
        interactor.presenter = presenter
        interactor.router = router
        presenter.viewController = viewController
        router.viewController = viewController
        router.dataStore = interactor
    }
    
    // MARK: IB
    
    override var tableViewStyle: UITableView.Style {
        return UIDevice.current.userInterfaceIdiom == .pad ? .grouped : super.tableViewStyle
    }
    
    // MARK: View lifecycle
    
    override func viewDidLoad() {
        super.viewDidLoad()
        
        doLoad()
    }
    
    // MARK: Do something
    
    private func doLoad() {
        setupUI()
        setupNavigationItems()
        
        let request = SettingsRoot.Load.Request()
        interactor?.doLoad(request: request)
    }
    
    private func setupUI() {
        title = "settings_title".localized
    }
    
    private func setupNavigationItems() {
        navigationItem.leftBarButtonItem = UIBarButtonItem(
            barButtonSystemItem: .cancel,
            target: self,
            action: #selector(onCancelButtonTap)
        )
    }
    
    @objc private func onCancelButtonTap() {
        let request = SettingsRoot.Cancel.Request()
        interactor?.doCancel(request: request)
    }
    
    // MARK: Display
    
    func displayLoad(viewModel: SettingsRoot.Load.ViewModel) {
        update(with: viewModel.tableViewModel)
    }
}
