@testable import xDrip
import XCTest

// swiftlint:disable implicitly_unwrapped_optional

final class EditCalibrationInteractorTests: XCTestCase {
    // MARK: Subject under test
    
    var sut: EditCalibrationInteractor!
    
    // MARK: Test lifecycle
    
    override func setUp() {
        super.setUp()
        setupEditCalibrationInteractor()
    }
    
    override func tearDown() {
        super.tearDown()
    }
    
    // MARK: Test setup
    
    func setupEditCalibrationInteractor() {
        sut = EditCalibrationInteractor()
    }
    
    // MARK: Test doubles
    
    final class EditCalibrationPresentationLogicSpy: EditCalibrationPresentationLogic {
        var presentLoadCalled = false
        
        func presentLoad(response: EditCalibration.Load.Response) {
            presentLoadCalled = true
        }
    }
    
    final class EditCalibrationRoutingLogicSpy: EditCalibrationRoutingLogic {
        func dismissScene() {
        }
        
        func showError(_ error: String) {
        }
        
        func showSuccessAndDismiss() {
        }
    }
    
    // MARK: Tests
    
    func testDoLoad() {
        // Given
        let spy = EditCalibrationPresentationLogicSpy()
        sut.presenter = spy
        let request = EditCalibration.Load.Request()
        
        // When
        sut.doLoad(request: request)
        
        // Then
        XCTAssertTrue(spy.presentLoadCalled, "doLoad(request:) should ask the presenter to format the result")
    }
}
