@testable import xDrip
import XCTest

final class EditTrainingViewControllerTests: XCTestCase {
    // MARK: Subject under test
    
    var sut: EditTrainingViewController!
    var window: UIWindow!
    
    // MARK: Test lifecycle
    
    override func setUp() {
        super.setUp()
        window = UIWindow()
        setupEditTrainingViewController()
    }
    
    override func tearDown() {
        window = nil
        super.tearDown()
    }
    
    // MARK: Test setup
    
    func setupEditTrainingViewController() {
        sut = EditTrainingViewController()
    }
    
    func loadView() {
        window.addSubview(sut.view)
        RunLoop.current.run(until: Date())
    }
    
    // MARK: Test doubles
    
    final class EditTrainingBusinessLogicSpy: EditTrainingBusinessLogic {
        var doLoadCalled = false
        var doDoneCalled = false
        var doCencelCalled = false
        
        func doLoad(request: EditTraining.Load.Request) {
            doLoadCalled = true
        }
        
        func doDone(request: EditTraining.Done.Request) {
            doDoneCalled = true
        }
        
        func doCancel(request: EditTraining.Cancel.Request) {
            doCencelCalled = true
        }
    }
    
    // MARK: Tests
    
    func testShouldDoLoadWhenViewIsLoaded() {
        // Given
        let spy = EditTrainingBusinessLogicSpy()
        sut.interactor = spy
        
        // When
        loadView()
        
        // Then
        XCTAssertTrue(spy.doLoadCalled, "viewDidLoad() should ask the interactor to do load")
    }
    
    func testDisplayLoad() {
        // Given
        let viewModel = EditTraining.Load.ViewModel(title: "", headerTitle: "", cells: [])
        
        // When
        loadView()
        sut.displayLoad(viewModel: viewModel)
        
        // Then
    }
}
