@testable import xDrip
import XCTest

// swiftlint:disable implicitly_unwrapped_optional

final class HomeRouterTests: XCTestCase {
    // MARK: Subject under test
    
    var sut: HomeRouter!
    
    // MARK: Test lifecycle
    
    override func setUp() {
        super.setUp()
        sut = HomeRouter()
    }
    
    // MARK: Test doubles
    
    final class ViewControllerSpy: HomeViewController {
        var lastPresentedViewController: UIViewController?
        
        override func present(_ viewControllerToPresent: UIViewController,
                              animated flag: Bool,
                              completion: (() -> Void)? = nil) {
            lastPresentedViewController = viewControllerToPresent
        }
    }
    
    // MARK: Tests
    
    func testRootToBolusEntriesList() {
        // Given
        let spy = ViewControllerSpy()
        sut.viewController = spy
        
        // When
        sut.routeToBolusEntriesList()
        
        // Then
        let entriesListNavController = spy.lastPresentedViewController as? UINavigationController
        XCTAssert(entriesListNavController?.viewControllers[0] is EntriesListViewController)
    }
    
    func testRootToCarbsEntriesList() {
        // Given
        let spy = ViewControllerSpy()
        sut.viewController = spy
        
        // When
        sut.routeToCarbsEntriesList()
        
        // Then
        let entriesListNavController = spy.lastPresentedViewController as? UINavigationController
        XCTAssert(entriesListNavController?.viewControllers[0] is EntriesListViewController)
    }
    
    func testRootToTrainingsEntriesList() {
        // Given
        let spy = ViewControllerSpy()
        sut.viewController = spy
        
        // When
        sut.routeToTrainingEntriesList()
        
        // Then
        let entriesListNavController = spy.lastPresentedViewController as? UINavigationController
        XCTAssert(entriesListNavController?.viewControllers[0] is EntriesListViewController)
    }
    
    func testRootToBasalsEntriesList() {
        // Given
        let spy = ViewControllerSpy()
        sut.viewController = spy
        
        // When
        sut.routeToBasalEntriesList()
        
        // Then
        let entriesListNavController = spy.lastPresentedViewController as? UINavigationController
        XCTAssert(entriesListNavController?.viewControllers[0] is EntriesListViewController)
    }
}
