@testable import xDrip
import XCTest

final class InitialSetupViewControllerTests: XCTestCase {
    // MARK: Subject under test
    
    var sut: InitialSetupViewController!
    var window: UIWindow!
    
    // MARK: Test lifecycle
    
    override func setUp() {
        super.setUp()
        window = UIWindow()
        setupInitialSetupViewController()
    }
    
    override func tearDown() {
        window = nil
        super.tearDown()
    }
    
    // MARK: Test setup
    
    func setupInitialSetupViewController() {
        sut = InitialSetupViewController()
    }
    
    func loadView() {
        window.addSubview(sut.view)
        RunLoop.current.run(until: Date())
    }
    
    // MARK: Test doubles
    
    final class InitialSetupBusinessLogicSpy: InitialSetupBusinessLogic {
        func doBeginSetup(request: InitialSetup.BeginSetup.Request) {
            
        }
        
        func doSkipSetup(request: InitialSetup.SkipSetup.Request) {
            
        }
        
        func doSelectDeviceMode(request: InitialSetup.SelectDeviceMode.Request) {
            
        }
        
        func doSelectInjectionType(request: InitialSetup.SelectInjectionType.Request) {
            
        }
        
        func doSaveSettings(request: InitialSetup.SaveSettings.Request) {
            
        }
        
        func doSelectDeviceType(request: InitialSetup.SelectDevice.Request) {
            
        }
        
        func doCompleteCustomDeviceStep(request: InitialSetup.CompleteCustomDeviceStep.Request) {
            
        }
        
        var doLoadCalled = false
        
        func doLoad(request: InitialSetup.Load.Request) {
            doLoadCalled = true
        }
    }
    
    // MARK: Tests
    
    func testShouldDoLoadWhenViewIsLoaded() {
        // Given
        let spy = InitialSetupBusinessLogicSpy()
        sut.interactor = spy
        
        // When
        loadView()
        
        // Then
        XCTAssertTrue(spy.doLoadCalled, "viewDidLoad() should ask the interactor to do load")
    }
    
    func testDisplayLoad() {
        // Given
        let viewModel = InitialSetup.Load.ViewModel()
        
        // When
        loadView()
        sut.displayLoad(viewModel: viewModel)
        
        // Then
    }
}
