@testable import xDrip
import XCTest

final class AddInjectionInteractorTests: XCTestCase {
    // MARK: Subject under test
    
    var sut: AddInjectionInteractor!
    
    // MARK: Test lifecycle
    
    override func setUp() {
        super.setUp()
        setupAddInjectionInteractor()
    }
    
    override func tearDown() {
        super.tearDown()
    }
    
    // MARK: Test setup
    
    func setupAddInjectionInteractor() {
        sut = AddInjectionInteractor()
    }
    
    // MARK: Test doubles
    
    final class AddInjectionPresentationLogicSpy: AddInjectionPresentationLogic {
        var presentLoadCalled = false
        
        func presentLoad(response: AddInjection.Load.Response) {
            presentLoadCalled = true
        }
    }
    
    final class AddInjectionRoutingLogicSpy: AddInjectionRoutingLogic {
        
    }
    
    // MARK: Tests
    
    func testDoLoad() {
        // Given
        let spy = AddInjectionPresentationLogicSpy()
        sut.presenter = spy
        let request = AddInjection.Load.Request()
        
        // When
        sut.doLoad(request: request)
        
        // Then
        XCTAssertTrue(spy.presentLoadCalled, "doLoad(request:) should ask the presenter to format the result")
    }
}
