@testable import xDrip
import XCTest

// swiftlint:disable implicitly_unwrapped_optional

final class SettingsAlertSoundInteractorTests: XCTestCase {
    // MARK: Subject under test
    
    var sut: SettingsAlertSoundInteractor!
    
    // MARK: Test lifecycle
    
    override func setUp() {
        super.setUp()
        setupSettingsAlertSoundInteractor()
    }
    
    override func tearDown() {
        super.tearDown()
    }
    
    // MARK: Test setup
    
    func setupSettingsAlertSoundInteractor() {
        sut = SettingsAlertSoundInteractor()
    }
    
    // MARK: Test doubles
    
    final class SettingsAlertSoundPresentationLogicSpy: SettingsAlertSoundPresentationLogic {
        var presentLoadCalled = false
        
        func presentLoad(response: SettingsAlertSound.Load.Response) {
            presentLoadCalled = true
        }
    }
    
    final class SettingsAlertSoundRoutingLogicSpy: SettingsAlertSoundRoutingLogic {        
    }
    
    // MARK: Tests
    
    func testDoLoad() {
        // Given
        let spy = SettingsAlertSoundPresentationLogicSpy()
        sut.presenter = spy
        let request = SettingsAlertSound.Load.Request()
        
        // When
        sut.doLoad(request: request)
        
        // Then
        XCTAssertTrue(spy.presentLoadCalled, "doLoad(request:) should ask the presenter to format the result")
    }
}
