@testable import xDrip
import XCTest

// swiftlint:disable implicitly_unwrapped_optional

final class SettingsCloudTypesViewControllerTests: XCTestCase {
    // MARK: Subject under test
    
    var sut: SettingsCloudTypesViewController!
    var window: UIWindow!
    
    // MARK: Test lifecycle
    
    override func setUp() {
        super.setUp()
        window = UIWindow()
        setupSettingsCloudTypesViewController()
    }
    
    override func tearDown() {
        window = nil
        super.tearDown()
    }
    
    // MARK: Test setup
    
    func setupSettingsCloudTypesViewController() {
        sut = SettingsCloudTypesViewController()
    }
    
    func loadView() {
        window.addSubview(sut.view)
        RunLoop.current.run(until: Date())
    }
    
    // MARK: Test doubles
    
    final class SettingsCloudTypesBusinessLogicSpy: SettingsCloudTypesBusinessLogic {
        var doLoadCalled = false
        
        func doLoad(request: SettingsCloudTypes.Load.Request) {
            doLoadCalled = true
        }
    }
    
    final class SettingsCloudTypesRoutingLogicSpy: SettingsCloudTypesRoutingLogic {
        var routeToConfigurationCalled = false
        
        func routeToNightscoutCloudConfiguration() {
            routeToConfigurationCalled = true
        }
    }
    
    // MARK: Tests
    
    func testShouldDoLoadWhenViewIsLoaded() {
        // Given
        let spy = SettingsCloudTypesBusinessLogicSpy()
        sut.interactor = spy
        
        // When
        loadView()
        
        // Then
        XCTAssertTrue(spy.doLoadCalled, "viewDidLoad() should ask the interactor to do load")
    }
    
    func testDisplayLoad() {
        // Given
        let tableViewModel = BaseSettings.ViewModel(sections: [])
        let viewModel = SettingsCloudTypes.Load.ViewModel(tableViewModel: tableViewModel)
        
        // When
        loadView()
        sut.displayLoad(viewModel: viewModel)
        
        // Then
    }
    
    func testSelectionHandler() {
        loadView()
        
        let routerSpy = SettingsCloudTypesRoutingLogicSpy()
        
        if let interactor = sut.interactor as? SettingsCloudTypesInteractor {
            interactor.router = routerSpy
        }
        
        guard let tableView = sut.view.subviews.compactMap({ $0 as? UITableView }).first else {
            XCTFail("Cannot obtain tableView")
            return
        }
        // When
        tableView.callDidSelect(at: IndexPath(row: 0, section: 0))
        // Then
        XCTAssertTrue(routerSpy.routeToConfigurationCalled)
    }
}
