@testable import xDrip
import XCTest

final class SettingsModeFollowerPresenterTests: XCTestCase {
    // MARK: Subject under test
    
    var sut: SettingsModeFollowerPresenter!
    
    // MARK: Test lifecycle
    
    override func setUp() {
        super.setUp()
        setupSettingsModeFollowerPresenter()
    }
    
    override func tearDown() {
        super.tearDown()
    }
    
    // MARK: Test setup
    
    func setupSettingsModeFollowerPresenter() {
        sut = SettingsModeFollowerPresenter()
    }
    
    // MARK: Test doubles
    
    final class SettingsModeFollowerDisplayLogicSpy: SettingsModeFollowerDisplayLogic {
        var displayLoadCalled = false
        
        func displayLoad(viewModel: SettingsModeFollower.Load.ViewModel) {
            displayLoadCalled = true
        }
    }
    
    // MARK: Tests
    
    func testPresentLoad() {
        // Given
        let spy = SettingsModeFollowerDisplayLogicSpy()
        sut.viewController = spy
        let response = SettingsModeFollower.Load.Response()
        
        // When
        sut.presentLoad(response: response)
        
        // Then
        XCTAssertTrue(spy.displayLoadCalled, "presentLoad(response:) should ask the view controller to display the result")
    }
}
