@testable import xDrip
import XCTest

final class SettingsUnitsInteractorTests: XCTestCase {
    // MARK: Subject under test
    
    var sut: SettingsUnitsInteractor!
    
    // MARK: Test lifecycle
    
    override func setUp() {
        super.setUp()
        setupSettingsUnitsInteractor()
    }
    
    override func tearDown() {
        super.tearDown()
    }
    
    // MARK: Test setup
    
    func setupSettingsUnitsInteractor() {
        sut = SettingsUnitsInteractor()
    }
    
    // MARK: Test doubles
    
    final class SettingsUnitsPresentationLogicSpy: SettingsUnitsPresentationLogic {
        var presentLoadCalled = false
        
        func presentLoad(response: SettingsUnits.Load.Response) {
            presentLoadCalled = true
        }
        
        func presentSelected(response: SettingsUnits.Select.Response) { }
    }
    
    final class SettingsUnitsRoutingLogicSpy: SettingsUnitsRoutingLogic {
        
    }
    
    // MARK: Tests
    
    func testDoLoad() {
        // Given
        let spy = SettingsUnitsPresentationLogicSpy()
        sut.presenter = spy
        let request = SettingsUnits.Load.Request()
        
        // When
        sut.doLoad(request: request)
        
        // Then
        XCTAssertTrue(spy.presentLoadCalled, "doLoad(request:) should ask the presenter to format the result")
    }
