@testable import xDrip
import XCTest

// swiftlint:disable implicitly_unwrapped_optional

final class SettingsPumpUserInteractorTests: XCTestCase {
    // MARK: Subject under test
    
    var sut: SettingsPumpUserInteractor!
    
    // MARK: Test lifecycle
    
    override func setUp() {
        super.setUp()
        setupSettingsPumpUserInteractor()
    }
    
    override func tearDown() {
        super.tearDown()
    }
    
    // MARK: Test setup
    
    func setupSettingsPumpUserInteractor() {
        sut = SettingsPumpUserInteractor()
    }
    
    // MARK: Test doubles
    
    final class SettingsPumpUserPresentationLogicSpy: SettingsPumpUserPresentationLogic {
        var presentLoadCalled = false
        
        func presentLoad(response: SettingsPumpUser.Load.Response) {
            presentLoadCalled = true
        }
    }
    
    final class SettingsPumpUserRoutingLogicSpy: SettingsPumpUserRoutingLogic {
    }
    
    // MARK: Tests
    
    func testDoLoad() {
        // Given
        let spy = SettingsPumpUserPresentationLogicSpy()
        sut.presenter = spy
        let request = SettingsPumpUser.Load.Request()
        
        // When
        sut.doLoad(request: request)
        
        // Then
        XCTAssertTrue(spy.presentLoadCalled, "doLoad(request:) should ask the presenter to format the result")
    }
}
